import classes from './Counter.module.css';
import { useSelector, useDispatch } from 'react-redux'
import { counterAction } from '../store/index';

const Counter = () => {

  const counter = useSelector(state => state.counter)
  const show = useSelector(state => state.showCounter)

  const dispatch = useDispatch();

  const incrementHandler = () => {
    dispatch(counterAction.increment())
  }

  const decrementHandler = () => {
    dispatch(counterAction.decrement())
  }

  const increses = () => {
    dispatch(counterAction.increses(10))
  }

  const toggleCounterHandler = () => { 
    dispatch(counterAction.toggle())
  };


  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {show && <div className={classes.value}>Hi {counter}</div>}
      <div>
        <button type='button' onClick={incrementHandler}>Increment</button>
        <button type='button' onClick={increses}>IncresesByTen</button>
        <button type='button' onClick={decrementHandler}>Decrement</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
